import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { FaGoogle, FaArrowRight } from 'react-icons/fa';
import { Mail, Lock, Eye, EyeOff, Loader2 } from 'lucide-react';
import styled, { keyframes } from 'styled-components';
import axios from 'axios';

// Animations
const fadeIn = keyframes`
  from { opacity: 0; transform: translateY(20px); }
  to { opacity: 1; transform: translateY(0); }
`;

// Layout Components
const PageContainer = styled.div`
  min-height: 100vh;
  display: flex;
  background: white;
  overflow: hidden;

  @media (max-width: 968px) {
    flex-direction: column;
  }
`;

// Left Side - Branding
const BrandingSection = styled.div`
  flex: 1;
  background: linear-gradient(135deg, #022c22 0%, #064e3b 100%);
  position: relative;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 4rem;
  color: white;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    width: 600px;
    height: 600px;
    background: radial-gradient(circle, rgba(15, 193, 121, 0.15) 0%, transparent 70%);
    top: -100px;
    right: -100px;
    border-radius: 50%;
  }

  &::after {
    content: '';
    position: absolute;
    width: 400px;
    height: 400px;
    background: radial-gradient(circle, rgba(15, 193, 121, 0.1) 0%, transparent 70%);
    bottom: -50px;
    left: -100px;
    border-radius: 50%;
  }

  @media (max-width: 968px) {
    display: none;
  }
`;

const GlassCard = styled.div`
  background: rgba(255, 255, 255, 0.05);
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.1);
  padding: 3rem;
  border-radius: 24px;
  max-width: 480px;
  width: 100%;
  z-index: 10;
  animation: ${fadeIn} 0.8s ease-out;
`;

const BrandTitle = styled.h1`
  font-size: 3.5rem;
  font-weight: 700;
  margin-bottom: 1.5rem;
  background: linear-gradient(135deg, #ffffff 0%, #a7f3d0 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  line-height: 1.1;
`;

const BrandDescription = styled.p`
  font-size: 1.125rem;
  line-height: 1.6;
  color: #d1fae5;
  margin-bottom: 2rem;
  font-weight: 400;
`;

const FeatureList = styled.div`
  display: flex;
  gap: 1rem;
  flex-wrap: wrap;
`;

const FeatureTag = styled.span`
  background: rgba(15, 193, 121, 0.1);
  border: 1px solid rgba(15, 193, 121, 0.2);
  padding: 0.5rem 1rem;
  border-radius: 100px;
  font-size: 0.875rem;
  color: #34d399;
  display: flex;
  align-items: center;
  gap: 0.5rem;
`;

// Right Side - Form
const FormSection = styled.div`
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 2rem;
  background: #ffffff;
  position: relative;
`;

const FormWrapper = styled.div`
  width: 100%;
  max-width: 440px;
  animation: ${fadeIn} 0.6s ease-out;
`;

const MobileBrandHeader = styled.div`
  display: none;
  text-align: center;
  margin-bottom: 2rem;

  @media (max-width: 968px) {
    display: block;
  }

  h2 {
    color: #064e3b;
    font-size: 2rem;
    font-weight: 700;
  }
`;

const WelcomeText = styled.div`
  margin-bottom: 2.5rem;

  h2 {
    font-size: 2rem;
    color: #111827;
    margin-bottom: 0.5rem;
    font-weight: 700;
  }

  p {
    color: #6b7280;
    font-size: 1rem;
    
    a {
      color: #0fc179;
      text-decoration: none;
      font-weight: 600;
      transition: color 0.2s;
      
      &:hover {
        color: #059669;
      }
    }
  }
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
`;

const InputGroup = styled.div`
  position: relative;
`;

const Label = styled.label`
  display: block;
  font-size: 0.875rem;
  font-weight: 500;
  color: #374151;
  margin-bottom: 0.5rem;
`;

const InputWrapper = styled.div`
  position: relative;
  
  svg {
    position: absolute;
    left: 1rem;
    top: 50%;
    transform: translateY(-50%);
    color: #9ca3af;
    transition: color 0.2s;
    width: 20px;
    height: 20px;
  }

  &:focus-within svg {
    color: #0fc179;
  }
`;

const Input = styled.input`
  width: 100%;
  padding: 0.875rem 1rem 0.875rem 3rem;
  border: 1.5px solid #e5e7eb;
  border-radius: 12px;
  font-size: 1rem;
  transition: all 0.2s ease;
  background: #f9fafb;
  color: #1f2937;

  &:focus {
    outline: none;
    border-color: #0fc179;
    background: #ffffff;
    box-shadow: 0 0 0 4px rgba(15, 193, 121, 0.1);
  }

  &::placeholder {
    color: #9ca3af;
  }
`;

const ToggleButton = styled.button`
  position: absolute;
  right: 1rem;
  top: 50%;
  transform: translateY(-50%);
  background: none;
  border: none;
  color: #9ca3af;
  cursor: pointer;
  padding: 0;
  display: flex;
  align-items: center;

  &:hover {
    color: #4b5563;
  }
`;

const ForgotLink = styled(Link)`
  text-align: right;
  color: #0fc179;
  font-size: 0.875rem;
  font-weight: 600;
  text-decoration: none;
  
  &:hover {
    color: #059669;
    text-decoration: underline;
  }
`;

const SubmitButton = styled.button`
  background: linear-gradient(135deg, #0fc179 0%, #059669 100%);
  color: white;
  padding: 1rem;
  border: none;
  border-radius: 12px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0.75rem;
  box-shadow: 0 10px 20px rgba(15, 193, 121, 0.2);

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 15px 25px rgba(15, 193, 121, 0.3);
  }

  &:disabled {
    opacity: 0.7;
    cursor: not-allowed;
    transform: none;
  }

  svg.spinner {
    animation: spin 1s linear infinite;
  }

  @keyframes spin {
    from { transform: rotate(0deg); }
    to { transform: rotate(360deg); }
  }
`;

const Divider = styled.div`
  display: flex;
  align-items: center;
  text-align: center;
  margin: 1.5rem 0;
  color: #9ca3af;
  font-size: 0.875rem;

  &::before,
  &::after {
    content: '';
    flex: 1;
    border-bottom: 1px solid #e5e7eb;
  }

  &::before {
    margin-right: 1rem;
  }

  &::after {
    margin-left: 1rem;
  }
`;

const GoogleBtn = styled.button`
  width: 100%;
  background: white;
  border: 1.5px solid #e5e7eb;
  color: #374151;
  padding: 0.875rem;
  border-radius: 12px;
  font-weight: 600;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0.75rem;
  cursor: pointer;
  transition: all 0.2s;
  font-size: 0.95rem;

  &:hover {
    background: #f9fafb;
    border-color: #d1d5db;
  }
`;

const OTPInputContainer = styled.div`
  display: flex;
  gap: 0.75rem;
  justify-content: center;
  margin: 2rem 0;
`;

const OTPDigit = styled.input`
  width: 50px;
  height: 60px;
  border: 2px solid #e5e7eb;
  border-radius: 12px;
  font-size: 1.5rem;
  text-align: center;
  font-weight: 700;
  color: #111827;
  transition: all 0.2s;
  background: #f9fafb;

  &:focus {
    outline: none;
    border-color: #0fc179;
    background: white;
    box-shadow: 0 0 0 4px rgba(15, 193, 121, 0.1);
    transform: translateY(-2px);
  }
`;

const Login = () => {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [showOTPVerification, setShowOTPVerification] = useState(false);
  const [requiresVerification, setRequiresVerification] = useState(false);
  const [otp, setOtp] = useState('');

  const { login } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const result = await login(formData.email, formData.password);
      if (result.success) {
        navigate('/dashboard');
      } else {
        setError(result.message || 'Invalid credentials');
      }
    } catch (error) {
      setError('Connection failed. Please try again.');
    }
    setLoading(false);
  };

  const handleOTPSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const response = await axios.post('/auth/verify-login', {
        email: formData.email,
        otp: otp
      });
      if (response.data.success) {
        localStorage.setItem('token', response.data.token);
        localStorage.setItem('user', JSON.stringify(response.data.user));
        window.location.href = '/dashboard';
      }
    } catch (error) {
      setError('Invalid verification code');
    }
    setLoading(false);
  };

  const handleGoogleLogin = () => {
    window.location.href = `${process.env.REACT_APP_API_URL || 'http://localhost:5001'}/auth/google`;
  };

  return (
    <PageContainer>
      {/* Left Side: Branding */}
      <BrandingSection>
        <GlassCard>
          <BrandTitle>DataLeaf</BrandTitle>
          <BrandDescription>
            Unlock the power of your opinions. Join our global community of insights and start earning rewards today.
          </BrandDescription>
          <FeatureList>
            <FeatureTag>Global Surveys</FeatureTag>
            <FeatureTag>Instant Rewards</FeatureTag>
            <FeatureTag>Secure Data</FeatureTag>
          </FeatureList>
        </GlassCard>
      </BrandingSection>

      {/* Right Side: Form */}
      <FormSection>
        <FormWrapper>
          <MobileBrandHeader>
            <h2>DataLeaf</h2>
          </MobileBrandHeader>

          {!showOTPVerification && !requiresVerification ? (
            <>
              <WelcomeText>
                <h2>Welcome back</h2>
                <p>New to DataLeaf? <Link to="/register">Create an account</Link></p>
              </WelcomeText>

              <Form onSubmit={handleSubmit}>
                <InputGroup>
                  <Label>Email Address</Label>
                  <InputWrapper>
                    <Mail />
                    <Input
                      type="email"
                      name="email"
                      placeholder="name@company.com"
                      value={formData.email}
                      onChange={handleChange}
                      required
                    />
                  </InputWrapper>
                </InputGroup>

                <InputGroup>
                  <Label>Password</Label>
                  <InputWrapper>
                    <Lock />
                    <Input
                      type={showPassword ? 'text' : 'password'}
                      name="password"
                      placeholder="Enter your password"
                      value={formData.password}
                      onChange={handleChange}
                      required
                    />
                    <ToggleButton type="button" onClick={() => setShowPassword(!showPassword)}>
                      {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
                    </ToggleButton>
                  </InputWrapper>
                </InputGroup>

                <ForgotLink to="/forgot-password">Forgot password?</ForgotLink>

                {error && <div style={{ color: '#ef4444', fontSize: '0.875rem', marginTop: '-0.5rem' }}>{error}</div>}

                <SubmitButton type="submit" disabled={loading}>
                  {loading ? (
                    <>
                      <Loader2 className="spinner" size={20} />
                      Signing in...
                    </>
                  ) : (
                    <>
                      Sign In
                      <FaArrowRight />
                    </>
                  )}
                </SubmitButton>
              </Form>

              <Divider>Or continue with</Divider>

              <GoogleBtn onClick={handleGoogleLogin}>
                <FaGoogle style={{ color: '#DB4437' }} />
                Sign in with Google
              </GoogleBtn>
            </>
          ) : (
            <Form onSubmit={handleOTPSubmit}>
              <WelcomeText>
                <h2>Verification</h2>
                <p>Enter the 6-digit code sent to <strong>{formData.email}</strong></p>
              </WelcomeText>

              <OTPInputContainer>
                <OTPDigit
                  type="text"
                  maxLength={6}
                  value={otp}
                  placeholder="------"
                  onChange={(e) => setOtp(e.target.value.replace(/\D/g, '').slice(0, 6))}
                  autoFocus
                />
              </OTPInputContainer>

              {error && <div style={{ color: '#ef4444', textAlign: 'center', marginBottom: '1rem' }}>{error}</div>}

              <SubmitButton type="submit" disabled={loading || otp.length !== 6}>
                {loading ? 'Verifying...' : 'Verify Login'}
              </SubmitButton>

              <div style={{ textAlign: 'center', marginTop: '1.5rem' }}>
                <button
                  type="button"
                  onClick={() => { setShowOTPVerification(false); setRequiresVerification(false); }}
                  style={{ background: 'none', border: 'none', color: '#6b7280', cursor: 'pointer', fontWeight: 500 }}
                >
                  Back to Login
                </button>
              </div>
            </Form>
          )}
        </FormWrapper>
      </FormSection>
    </PageContainer>
  );
};

export default Login;
